package testCases;import org.openqa.selenium.By;import org.openqa.selenium.WebDriver;import org.openqa.selenium.chrome.ChromeDriver;import org.openqa.selenium.support.FindBy;import java.time.Duration;public class Test {    public static void main(String[] args) throws InterruptedException {        WebDriver driver = new ChromeDriver();        driver.get("https://demo.opencart.com/");        driver.manage().timeouts().implicitlyWait(Duration.ofSeconds(10));        driver.manage().window().maximize();        Thread.sleep(5000);        driver.findElement(By.xpath("//span[normalize-space()='My Account']")).click();        driver.findElement(By.xpath("//a[normalize-space()='Login']")).click();    }}